mapper.select:
select

from 

where
	1=1

	and CORP_ID IN (
		SELECT CORP_ID FROM SYS_CORP m WHERE CORP_ID = #{pd.CORP_ID}
		UNION ALL
		SELECT CORP_ID FROM SYS_CORP WHERE CORP_ID = (
		SELECT PARENT_ID FROM SYS_CORP WHERE CORP_ID = #{pd.CORP_ID}
		)
		UNION ALL
		SELECT CORP_ID FROM SYS_CORP WHERE CORP_ID = (
		SELECT PARENT_ID FROM SYS_CORP WHERE CORP_ID = (
		SELECT PARENT_ID FROM SYS_CORP WHERE CORP_ID = #{pd.CORP_ID}
		))
		UNION ALL
		SELECT CORP_ID FROM SYS_CORP WHERE CORP_ID = (
		SELECT PARENT_ID FROM SYS_CORP WHERE CORP_ID = (
		SELECT PARENT_ID FROM SYS_CORP WHERE CORP_ID = (
		SELECT PARENT_ID FROM SYS_CORP WHERE CORP_ID = #{pd.CORP_ID}
		)))
	)



	and PARENT_ID = #{pd.DIMENTIONS_ID}


	and
		(
		 NAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
		 or 
		 NAME_EN LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') 
		 or
		 BIANMA LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
		)

order by ORDER_BY

select 

from 

where 1=1

	and CORP_ID IN (
		SELECT CORP_ID FROM SYS_CORP m WHERE CORP_ID = #{CORP_ID}
		UNION ALL
		SELECT CORP_ID FROM SYS_CORP WHERE CORP_ID = (
		SELECT PARENT_ID FROM SYS_CORP WHERE CORP_ID = #{CORP_ID}
		)
		UNION ALL
		SELECT CORP_ID FROM SYS_CORP WHERE CORP_ID = (
		SELECT PARENT_ID FROM SYS_CORP WHERE CORP_ID = (
		SELECT PARENT_ID FROM SYS_CORP WHERE CORP_ID = #{CORP_ID}
		))
		UNION ALL
		SELECT CORP_ID FROM SYS_CORP WHERE CORP_ID = (
		SELECT PARENT_ID FROM SYS_CORP WHERE CORP_ID = (
		SELECT PARENT_ID FROM SYS_CORP WHERE CORP_ID = (
		SELECT PARENT_ID FROM SYS_CORP WHERE CORP_ID = #{CORP_ID}
		)))
	)


	AND PARENT_ID = #{PARENT_ID}
order by ORDER_BY

select
	count(*) zs
from 
	${thisTable}
where
	BIANMA = #{BIANMA}
