mapper.select:
select

from 

where 1=1

	and CORP_ID IN (
		SELECT m.CORP_ID FROM SYS_CORP m WHERE m.CORP_ID = #{pd.CORP_ID}
		UNION ALL
		SELECT CORP_ID FROM SYS_CORP WHERE PARENT_ID = #{pd.CORP_ID}
		UNION ALL
		SELECT CORP_ID FROM SYS_CORP WHERE PARENT_ID IN (
		SELECT CORP_ID FROM SYS_CORP WHERE PARENT_ID = #{pd.CORP_ID}
		)
		UNION ALL
		SELECT CORP_ID FROM SYS_CORP WHERE PARENT_ID IN (
		SELECT CORP_ID FROM SYS_CORP WHERE PARENT_ID IN (
		SELECT CORP_ID FROM SYS_CORP WHERE PARENT_ID = #{pd.CORP_ID}
		))
	)



	and
		(

		)

select
	a.TRANS_ID,
	a.AUDIT_USER_IDS,
	a.AUDIT_USER,
	a.AUDIT_TIME,
	a.AUDIT_STATUS,
	a.NODE_ID,
	b.USERNAME,
	b.NAME
from 
 a
left join sys_user b on a.AUDIT_USER = b.role_id
where 1=1

	and a.CORP_ID IN (
		SELECT m.CORP_ID FROM SYS_CORP m WHERE m.CORP_ID = #{CORP_ID}
		UNION ALL
		SELECT CORP_ID FROM SYS_CORP WHERE PARENT_ID = #{CORP_ID}
		UNION ALL
		SELECT CORP_ID FROM SYS_CORP WHERE PARENT_ID IN (
		SELECT CORP_ID FROM SYS_CORP WHERE PARENT_ID = #{CORP_ID}
		)
		UNION ALL
		SELECT CORP_ID FROM SYS_CORP WHERE PARENT_ID IN (
		SELECT CORP_ID FROM SYS_CORP WHERE PARENT_ID IN (
		SELECT CORP_ID FROM SYS_CORP WHERE PARENT_ID = #{CORP_ID}
		))
	)



    and a.TRANS_ID = #{TRANS_ID}


    and a.AUDIT_USER_IDS = #{AUDIT_USER_IDS}
